const crypto = require('crypto');
const reveal = $json["body"].vote;
const salt = $json["body"].salt;
const commitHash = $json["body"].commitHash;
const hashCheck = crypto.createHash('sha256').update(reveal+salt).digest('hex');
if(hashCheck === commitHash){
  return [{ json: { valid: true, claimId: $json["body"].claimId, juror: $json["body"].juror, vote: reveal } }];
} else {
  return [{ json: { valid: false } }];
}

const { claimant, claimType, evidenceHash } = items[0].json;
if (!claimant || !claimType || !evidenceHash) {
  throw new Error('Missing required fields');
}
return items;

const crypto = require('crypto');
const { vote, salt, commitHash } = items[0].json;
const hash = crypto.createHash('sha256').update(vote + salt).digest('hex');
if (hash !== commitHash) {
  throw new Error('Reveal does not match commit');
}
return items;

const votes = $json.votes;
const oracle = $json.oracle;
let approve = votes.filter(v => v === 'approve').length;
let deny = votes.filter(v => v === 'deny').length;
if (oracle.verified && approve > deny) {
  return [{json: { decision: 'approved' }}];
} else {
  return [{json: { decision: 'denied' }}];
}

const claim = {
  claimId: $json.claimId,
  userId: $json.userId,
  amount: $json.amount,
  evidenceUrl: $json.evidenceUrl,
  status: 'submitted'
};
return [{json: claim}];

const flagged = [];
if ($json.amount > 10000) flagged.push('high_amount');
return [{json: { ...$json, fraudFlags: flagged }}];

const votes = $json.commitVotes || [];
const yes = votes.filter(v=>v==='yes').length;
const no = votes.length - yes;
const resolution = yes>no ? 'approved' : 'rejected';
return [{json:{...$json, resolution}}];

const crypto = require('crypto');

const secret = $env.HMAC_SECRET;
const sigHeader = $json.headers['x-claimchain-signature'];
const payload = JSON.stringify($json.body);

const hash = crypto.createHmac('sha256', secret).update(payload).digest('hex');

if (hash !== sigHeader) {
  throw new Error('Invalid HMAC signature');
}

return items;

const key = $json.body.idempotencyKey;
if (!key) throw new Error('Missing idempotencyKey');

// Simple in-memory store (replace with Redis/DB for production)
global.store = global.store || {};
if (global.store[key]) {
  return [];
}

global.store[key] = true;
return items;

const user = $json.body.user || 'anon';
const now = Date.now();
global.rl = global.rl || {};

if (!global.rl[user]) {
  global.rl[user] = [];
}

// keep requests in the last 60s
global.rl[user] = global.rl[user].filter(t => now - t < 60000);

if (global.rl[user].length >= 5) {
  throw new Error('Rate limit exceeded');
}

global.rl[user].push(now);
return items;

global.dlq = global.dlq || [];
// push failed payloads into DLQ
global.dlq.push({ timestamp: Date.now(), payload: $json });
return items;

global.dlq = global.dlq || [];
const replayed = global.dlq.splice(0, global.dlq.length);
return replayed.map(r => ({ json: r.payload }));

const dlq = global.dlq || [];
if (dlq.length > 0) {
  return [{ json: { dlqSize: dlq.length } }];
}
return [];

